import clsx from 'clsx';
import styles from './TableForm.module.scss'
import { useState } from 'react';


const TableForm = () => {
  const [status, setStatus] = useState('');
  const [peopleMax, setPeopleMax] = useState('');
  const [peopleSitting, setPeopleSitting] = useState('');
  const [bill, setBill] = useState('');

  return (
    <div className="container">
      <div >
        <h3 className="mt-4 pt-4">Status:</h3>
        <select onChange={e => setStatus(e.target.value)}>
          <option>Free</option>
          <option>Busy</option>
          <option>Cleaning</option>
          <option>Reserved</option>
        </select>
      </div>

      <div>
        <h3 className="mt-4 pt-4">People:</h3>
        <input type="number" value={peopleSitting} onChange={e =>
          setPeopleSitting(e.target.value)}></input><span>/</span><input type="number" value={peopleMax} onChange={e =>
            setPeopleMax(e.target.value)}></input>
      </div>

      <div>
        <h3 className="mt-4 pt-4">Bill</h3>
        <span>$</span><input type="number" value={bill} onChange={e =>
          setBill(e.target.value)}></input>
      </div>

      <button className={clsx("btn-default btn-lg mt-4", styles.mybtn)}>Update</button>


    </div >
  )

}

export default TableForm;
