import clsx from 'clsx';
import styles from './TableForm.module.scss'
import PeopleInput from './../PeopleInput/PeopleInput'
import BillInput from './../BillInput/BillInput'
import Status from './../Status/Status'
import { requestTableBill } from './../redux/tablesRedux';
import { requestTablePeopleMax } from './../redux/tablesRedux';
import { requestTablePeopleSitting } from './../redux/tablesRedux';
import { requestTableStatus } from './../redux/tablesRedux';
import { useState } from 'react';
import { useDispatch } from 'react-redux';



const TableForm = (props) => {
  const [status] = useState('');
  const [bill] = useState('');
  const [peopleMax] = useState('');
  const [peopleSitting] = useState('');

  const dispatch = useDispatch();

  const handleSubmit = e => {
    e.preventDefault();
    dispatch(requestTableBill({ bill }));
    dispatch(requestTablePeopleMax({ peopleMax }))
    dispatch(requestTablePeopleSitting({ peopleSitting }))
    dispatch(requestTableStatus({ status }))
  }
  return (
    <form className="container" onSubmit={handleSubmit}>
      <h1 className="mt-4">Table {props.id}</h1>
      <Status />
      <PeopleInput />
      <BillInput />

      <button className={clsx("btn-default btn-lg mt-4", styles.mybtn)}>Update</button>
    </form >
  )

}

export default TableForm;
