import styles from './Table.module.scss'
import clsx from 'clsx';
import { useSelector } from 'react-redux';
import { getAllTables } from '../../redux/store';
import { Link } from 'react-router-dom';


const Table = (props) => {
  const tables = useSelector(state => getAllTables(state));
  return (
    <div className="container">

      <li className={clsx("d-flex justify-space-between p-4 m-4", styles.table)}>
        <div className="row">
          <h2 className="col-2 m-4">Table {props.id} </h2>
          <p className="col-2 m-5"><span className={styles.status}>Status:</span> {props.status} </p>
          {tables.map(table => (
            <Link to={'/tables/' + table.id}><button className={clsx("btn-default, btn-lg")}>Show more</button></Link>
          ))}
        </div>
      </li>

    </div>
  )
}

export default Table;
