import clsx from 'clsx';
import styles from './TableForm.module.scss'
import PeopleInput from './../PeopleInput/PeopleInput'
import BillInput from './../BillInput/BillInput'
import Status from './../Status/Status'
import { requestTableParams } from '../../redux/tablesRedux';
import { useParams } from 'react-router';


const TableForm = (props) => {
  const tableId = useParams();

  const handleSubmit = e => {
    e.preventDefault();
    dispatchEvent(requestTableParams(props.status, props.peopleMax, props.peopleSitting, props.bill))
  }
  return (
    <form className="container" onSubmit={handleSubmit}>
      <h1 className="mt-4">Table {tableId}</h1>
      <Status />
      <PeopleInput />
      <BillInput />

      <button className={clsx("btn-default btn-lg mt-4", styles.mybtn)}>Update</button>
    </form >
  )

}

export default TableForm;
