import clsx from 'clsx';
import styles from './TableForm.module.scss'
import TextInput from './../TextInput/TextInput';
import { requestTableParams, getTableById } from '../../redux/tablesRedux';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';



const TableForm = () => {
  const tableId = useParams();
  const table = useSelector(state => getTableById(state, tableId));
  const [status, setStatus] = useState('');
  const [bill, setBill] = useState('');
  const [peopleMax, setPeopleMax] = useState('');
  const [peopleSitting, setPeopleSitting] = useState('');

  const dispatch = useDispatch();
  const handleSubmit = e => {
    e.preventDefault();
    dispatch(requestTableParams({ tableId, status, peopleSitting, peopleMax, bill }));



  }
  return (
    <form className="container" onSubmit={handleSubmit}>
      <h1 className="mt-4">Table {`${table.id}`}</h1>

      <div >
        <label className={styles.paramtitle}>Status:</label>
        <select value={status} onChange={e => setStatus(e.target.value)}>
          <option defaultValue={table.status}>{`${status}`}</option>
          <option value="Free">Free</option>
          <option value="Busy">Busy</option>
          <option value="Cleaning">Cleaning</option>
          <option value="Reserved">Reserved</option>
        </select>
      </div>
      <div>
        <label className={styles.paramtitle}>People:</label> <TextInput value={table.peopleSitting} onChange={e => setPeopleSitting(e.target.value)} />
        <span>/</span>
        <TextInput value={table.peopleMax} onChange={e => setPeopleMax(e.target.value)} />
      </div>
      <div>
        <label className={styles.paramtitle}>Bill:</label><span className="ml-4">$</span> <TextInput value={table.bill} onChange={e => setBill(e.target.value)} />
      </div>

      <button className={clsx("btn-default btn-lg mt-4", styles.mybtn)}>Update</button>
    </form >
  )

}

export default TableForm;
