import clsx from 'clsx';
import styles from './TableForm.module.scss'
import { useState } from 'react';
import { updateTableParams } from '../../redux/store'
import { useDispatch } from 'react-redux';
import TextInput from './../TextInput/TextInput';


const TableForm = () => {

  const dispatch = useDispatch();

  const [status, setStatus] = useState('');
  const [peopleMax, setPeopleMax] = useState('');
  const [peopleSitting, setPeopleSitting] = useState('');
  const [bill, setBill] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    dispatch(updateTableParams({ status, peopleMax, peopleSitting, bill }));
  }
  return (
    <form className="container" onSubmit={handleSubmit}>
      <div >
        <h3 className="mt-4 pt-4">Status:</h3>
        <select value={status} onChange={e => setStatus(e.target.value)}>
          <option value="Free">Free</option>
          <option value="Busy">Busy</option>
          <option value="Cleaning">Cleaning</option>
          <option value="Reserved">Reserved</option>
        </select>
      </div>

      <div>
        <label className={styles.paramtitle}>Title:</label> <TextInput value={peopleSitting} onChange={e => setPeopleSitting(e.target.value)} />
        <span>/</span>
        <TextInput value={peopleMax} onChange={e => setPeopleMax(e.target.value)} />
      </div>

      <div>
        <label className={styles.paramtitle}>Bill:</label> <TextInput value={bill} onChange={e => setBill(e.target.value)} />
      </div>

      <button className={clsx("btn-default btn-lg mt-4", styles.mybtn)}>Update</button>


    </form >
  )

}

export default TableForm;
