//selectors
export const getAllTables = (state) => state.tables;


// actions
const createActionName = actionName => `app/tables/${actionName}`;
const UPDATE_TABLES = createActionName('UPDATE_TABLES');
const UPDATE_STATUS = createActionName('UPDATE_STATUS');
const UPDATE_PEOPLE_MAX = createActionName('UPDATE_PEOPLE_MAX');
const UPDATE_PEOPLE_SITTING = createActionName('UPDATE_PEOPLE_SITTING');
const UPDATE_BILL = createActionName('UPDATE_BILL');

export const updateTables = payload => ({ type: UPDATE_TABLES, payload });
export const updateTableParams = payload => ({ type: UPDATE_TABLE_PARAMS, payload });

export const fetchTables = () => {
  return (dispatch) => {
    fetch('http://localhost:3131/api/tables')
      .then(res => res.json())
      .then(tables => dispatch(updateTables(tables)));
  };
};

export const requestTableParams = (newTable) => {
  return (dispatch) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newTable),
    };

    fetch('http://localhost:3131/tables', options)
      .then(() => dispatch(updateTableParams(newTable)))
  }
}

// action creators
const tablesReducer = (statePart = [], action) => {
  switch (action.type) {
    case UPDATE_TABLES:
      return [...action.payload];
    case UPDATE_TABLE_PARAMS:
      return [...statePart, { ...action.payload }];
    default:
      return statePart;
  };
};
export default tablesReducer;
